/** Scarab Accessor. 
 *
 * Outputs battery charge percentage and 
 *
 *  @accessor Scarab
 */

var WebSocket = require('webSocket');

/** Set up the accessor by defining the parameters, inputs, and outputs. */
exports.setup = function() {

  input('pose');

  output('battery', {
    type: 'int'
  });
  output('state', {
    type: 'string'
  });
  output('location');

  parameter('server', {
    type: 'string',
    value: 'localhost'
  });
  parameter('port', {
    type: 'int',
    value: 8080
  });
  parameter('topicPrefix', {
    type: 'string',
    value: '/scarab/lucy'
  });
}

var batteryClient = null;
var stateClient = null;
var locationClient = null;
var poseClient = null;

var seq = 0;

/** Initializes accessor by attaching functions to inputs. */
exports.initialize = function() {

  // Retreive the current battery charge status
  batteryClient = new WebSocket.Client({
    host: getParameter('server'),
    port: getParameter('port')
  });
  batteryClient.on('open', function () {
    // Subscribe to /scarab/name/diagnostics
    batteryClient.send({
        op: "subscribe",
        topic: getParameter('topicPrefix') + '/diagnostics'
    });
  });
  batteryClient.on('message', function (msg) {
    // Quick hack to find the charge of the battery.
    // Ideally this would be done in some better way, but this is all we
    // need for now.
    s = msg.msg.status[1].message;
    charge = parseInt(s.substr(0, s.indexOf('%')));
    send('battery', charge);
  });
  batteryClient.on('error', function(message) {
    error(message)
  });

  // Keep track of what the robot is doing
  stateClient = new WebSocket.Client({
    host: getParameter('server'),
    port: getParameter('port')
  });
  stateClient.on('open', function () {
    // Subscribe to /scarab/name/diagnostics
    stateClient.send({
        op: "subscribe",
        topic: getParameter('topicPrefix') + '/state'
    });
  });
  stateClient.on('message', function (msg) {
    // one of: IDLE, BUSY, STUCK, FAILED
    send('state', msg.msg.state);
  });
  stateClient.on('error', function(message) {
    error(message)
  });

  // Get location updates from the robot
  locationClient = new WebSocket.Client({
    host: getParameter('server'),
    port: getParameter('port')
  });
  locationClient.on('open', function () {
    // Subscribe to /scarab/name/pose
    locationClient.send({
        op: "subscribe",
        topic: getParameter('topicPrefix') + '/pose'
    });
  });
  locationClient.on('message', function (msg) {
    send('location', msg.msg.pose);
  });
  locationClient.on('error', function(message) {
    error(message)
  });

  // Get location updates from the robot
  poseClient = new WebSocket.Client({
    host: getParameter('server'),
    port: getParameter('port')
  });
  poseClient.on('open', function () {
    // Subscribe to /scarab/name/pose
    poseClient.send({
        op: 'advertise',
        topic: getParameter('topicPrefix') + '/goal',
        type: 'geometry_msgs/PoseStamped'
    });
  });
  poseClient.on('error', function(message) {
    error(message)
  });
  addInputHandler('pose', pose_in);
} 

var pose_in = function () {
  var v = get('pose');

  out = {
    op: 'publish',
    topic: getParameter('topicPrefix') + '/goal',
    msg: {
      'header': {
        'seq': seq++,
        'stamp': {
          'secs': 0,
          'nsecs': 0
        },
        'frame_id': 'map_hokuyo'
      },
      'pose': v
    }
  };

  poseClient.send(out);
}

exports.wrapup = function() {
  if (stateClient) {
    stateClient.removeAllListeners('open');
    stateClient.removeAllListeners('message');
    stateClient.removeAllListeners('close');
    stateClient.close();
    stateClient = null;
  }
  if (batteryClient) {
    batteryClient.removeAllListeners('open');
    batteryClient.removeAllListeners('message');
    batteryClient.removeAllListeners('close');
    batteryClient.close();
    batteryClient = null;
  }
  if (locationClient) {
    locationClient.removeAllListeners('open');
    locationClient.removeAllListeners('message');
    locationClient.removeAllListeners('close');
    locationClient.close();
    locationClient = null;
  }
  if (poseClient) {
    poseClient.send({
        op: 'unadvertise',
        topic: getParameter('topicPrefix') + '/goal'
    });
    poseClient.removeAllListeners('open');
    poseClient.removeAllListeners('message');
    poseClient.removeAllListeners('close');
    poseClient.close();
    poseClient = null;
  }
}

// Copyright (c) 2014-2015 The Regents of the University of Michigan

/** Shifts X,Y,Z coordinates
 *
 *  @accessor CoordinateLinearTransform
 *
*/

var shifts = {
  X: 0,
  Y: 0,
  Z: 0
};

/** Define inputs and outputs. */
exports.setup = function() {
  //
  // I/O
  //
  input('Input', {
  });
  output('Output', {
  });

  //
  // Parameters
  //
  parameter('Xshift', {
    type: 'number',
    value: 0
  });
  parameter('Yshift', {
    type: 'number',
    value: 0
  });
  parameter('Zshift', {
    type: 'number',
    value: 0
  });
}

exports.initialize = function () {

  addInputHandler('Input', Input_in);

  shifts.X = getParameter('Xshift');
  shifts.Y = getParameter('Yshift');
  shifts.Z = getParameter('Zshift');
}

var Input_in = function () {
  var v = get('Input');

  var axes = ['X', 'Y', 'Z'];
  for (i=0; i<axes.length; i++) {
    var axis = axes[i];

    if (typeof v === 'object' && axis in v && v.hasOwnProperty(axis)) {
      v[axis] += shifts[axis];
    }
  }

  send('Output', v);
}

// Copyright (c) 2014-2015 The Regents of the University of Michigan

/** Generate a stream of made up location data.
 *
 *  Outputs an object that looks like:
 *
 *      {
 *        id: <unique id per thing being localized>
 *        X:  <x coordinate>
 *        Y:  <x coordinate>
 *        Z:  <x coordinate>
 *      }
 *
 *  @accessor FakeLocationStream
 *
*/

// Which ID we should be generating location data for
var current_id;

// Last location
var x;
var y;
var z;

/** Define inputs and outputs. */
exports.setup = function() {
  //
  // I/O
  //
  input('ID', {
    type: "string",
    value: "1"
  });
  output('Location', {
  });

  //
  // Parameters
  //
  // How often to generate a new location value in seconds
  parameter('Period', {
    value: 1,
    type: 'number'
  });

  // Bounds of generated locations in meters
  parameter('MinX', {value: 0, type: 'number'});
  parameter('MaxX', {value: 10, type: 'number'});
  parameter('MinY', {value: 0, type: 'number'});
  parameter('MaxY', {value: 10, type: 'number'});
  parameter('MinZ', {value: 1, type: 'number'});
  parameter('MaxZ', {value: 2, type: 'number'});

  // How much any one parameter can vary between updates
  parameter('MaxVariance', {value: 0.2, type: 'number'});
}

exports.initialize = function () {

  addInputHandler('ID', ID_in);

  current_id = get('ID');
  x = getParameter('MinX');
  y = getParameter('MinY');
  z = getParameter('MinZ');

  // Use the period parameter to setup the loop that generates fake
  // location data.
  setInterval(update_location, getParameter('Period')*1000);
}

function getRandomArbitrary (min, max) {
  return Math.random() * (max - min) + min;
}

function getRandomInt (min, max) {
  return Math.floor(Math.random() * (max - min)) + min;
}

// Generates a new location value and sends to output based on parameters set
function update_location () {

  var j = getParameter('MaxVariance');
  var alter = getRandomArbitrary(j/2, j);
  var neg = getRandomInt(1, 3);
  if (neg == 1) {
    alter *= -1;
  }

  // Choose which axis to change on this iteration
  var whichone = getRandomInt(0, 3);

  if (whichone == 0) {
    // ALTER X
    var new_x = x + alter;
    if (new_x < getParameter('MinX')) new_x = getParameter('MinX');
    if (new_x > getParameter('MaxX')) new_x = getParameter('MaxX');
    x = new_x;

  } else if (whichone == 1) {
    // ALTER Y
    var new_y = y + alter;
    if (new_y < getParameter('MinY')) new_y = getParameter('MinY');
    if (new_y > getParameter('MaxY')) new_y = getParameter('MaxY');
    y = new_y;

  } else if (whichone == 2) {
    // ALTER Z
    var new_z = z + alter;
    if (new_z < getParameter('MinZ')) new_z = getParameter('MinZ');
    if (new_z > getParameter('MaxZ')) new_z = getParameter('MaxZ');
    z = new_z;

  }

  var loc = {
    id: current_id,
    X: x,
    Y: y,
    Z: z
  };

  send('Location', loc);

}


var ID_in  = function () {
  current_id = get('ID');
}

