/** This accessor starts a server that listens for web socket
 *  connection requests on the specified hostInterface and port.  The
 *  hostInterface is needed only if the host machine has more than one
 *  network interface (e.g. Ethernet and WiFi) and 'localhost' does
 *  not resolve to the desired interface.
 *
 *  <p>The output <code>connection</code> reports the when a
 *  connection is opened or closed.</p>
 *
 *  <p>When a message arrives on a connection, a <code>received</code>
 *  output is produced with that message.</p>
 *
 *  <p>When an input arrives on <code>toSend</code>, then a message is
 *  sent to one or all of the open socket connections.</p>
 *
 *  <p>When <code>wrapup()</code> is invoked, this accessor closes the
 *  server and all connections.</p>
 *
 *  <p>The messages can be any type that has a JSON representation.
 *  For incomming messages, this accessor assumes that the message is
 *  a string in UTF-8 that encodes a JSON object.</p>
 *
 *  <p>This accessor requires the module webSocket.</p>
 *
 *  @accessor WebSocketServer
 *  @parameter {string} hostInterface The IP address or domain name of the
 *    network interface to listen to.
 *  @parameter {number} port The port to listen to for connections.
 *  @input toSend The data to be sent to open sockets. If this is an object with 'socketID' field and a 'message' field, then send the value of the message field to the socket identified by the socketID field. If the input has any other form, then the message is broadcast to all open socket connections.
 *  @output connection An output produced when a connection opens or closes. The output is an object with two fields, a 'socketID', which is a unique ID for this client connection, and a 'status' field, which is the string 'open' or 'closed'.
 *  @output received A message received a client in the form of an object with two fields, a 'socketID', which is a unique ID for this client connection, and a 'message' field, which is the message received from the client.
 *  @author Hokeun Kim, Edward Lee 
 *  @version $$Id: WebSocketServer.js 234 2015-08-01 22:06:40Z eal $$ 
 */

var WebSocket = require('webSocket');
var server = null;
var running = false;

/** Sets up the accessor by defining inputs and outputs. */
exports.setup = function() {
    parameter('hostInterface', {
        value: "localhost", 
        type: "string" 
    });
    parameter('port', {
        value: 8080, 
        type: "int" 
    });
    input('toSend');
    output('received');
    output('connection');
}

var handle;
var sockets = [];

/** Starts the web socket and attaches functions to inputs and outputs. 
  * Adds an input handler on toSend that sends the input received to the right socket. */ 
exports.initialize = function() {
    if (!server) {
        server = new WebSocket.Server({'port':getParameter('port'),
                                       'hostInterface':getParameter('hostInterface')});
        server.on('listening', onListening);
        server.on('connection', onConnection);
        server.start();
    }
    running = true;

    handle = addInputHandler('toSend', function() {
        var data = get('toSend');
        // Careful: Don't do if (data) because if data === 0, then data is false.
        if (data !== null) {
            if ((data.socketID != null)  && (data.message != null)) {
                // data has the right form for a point-to-point send.
                if (sockets[data.socketID] && sockets[data.socketID].isOpen()) {
                    // id matches this socket.
                    console.log("Sending to socket id " 
                            + data.socketID
                            + " message: "
                            + data.message);
                    sockets[data.socketID].send(data.message);
                } else {
                    console.log('Socket with ID ' + data.socketID
                            + ' is not open. Discarding message: ' + data.message);
                }
            } else {
                // No socketID or message, so this is a broadcast message.
                var success = false;
                for (var id = 0; id < sockets.length; id++) {
                    if (sockets[id].isOpen()) {
                        console.log("Broadcasting to socket id " + id 
                                + " message: " + data);
                        sockets[id].send(data);
                        success = true;
                    }
                }
                if (!success) {
                    console.log('No open sockets. Discarding message: ' + data.message);
                }
            }
        }
    });
}

function onListening() {
    console.log('Server: Listening for socket connection requests.');
}

/** Executes when a connection has been establised.<br>
 *  Triggers an output on <code>'connection'</code>.
 *  Adds an event listener to the socket. */
function onConnection(socket) {
   //socketID is the index of the socket in the sockets array. 
    var socketID = sockets.length;
    console.log('Server: new socket established with ID: ' + socketID);
    send('connection', {'socketID':socketID, 'status':'open'});
    socket.on('message', function(message) {
        send('received', {'socketID':socketID, 'message':message});
    });
    socket.on('close', function(message) {
        send('connection', {'socketID':socketID, 'status':'closed'});
    });

    sockets.push(socket);    
}

/** Removes all inputHandlers from sockets.<br>
 * Unregisters event listeners from sockets.<br>
 * Closes server. */
exports.wrapup = function(){
    for (var i = 0; i < sockets.length; i++) {
        sockets[i].removeAllListeners();
    }

    sockets = [];
    removeInputHandler(handle); 

    if (server != null) {
        server.removeAllListeners();
        server.close();
        server = null;
    }
}

// Copyright (c) 2014-2015 The Regents of the University of Michigan

/** Switching Network
 *
 * Takes in control assignments in the form of {input: key_value,
 *                                              output: output_index,
 *                                              type: add|remove}
 * and maps inputs to outputs
 *
 *  @accessor OpSwitching
 *
*/

var key;

var matrix = {};


/** Define inputs and outputs. */
exports.setup = function() {
  //
  // I/O
  //
  input('Input');

  input('Select');

  output('Output0');
  output('Output1');
  output('Output2');
  output('Output3');
  output('Output4');
  output('Output5');

  parameter('Key', {
    type: 'string',
    value: 'id'
  });
}

exports.initialize = function () {
  key = getParameter('Key');

  addInputHandler('Select', Select_in);
  addInputHandler('Input', Input_in);
}

var Select_in = function () {
  var s = get('Select');

  var t = s['type'];
  var i = s['input'];
  var o = 'Output' + s['output'];

  if (!(i in matrix && matrix.hasOwnProperty(i))) {
    // Create the array if it doesn't exist
    matrix[i] = [];
  }

  if (t == 'remove') {
    // Get rid of this connection
    var destarr = matrix[i];
    var index = destarr.indexOf(o);
    if (index > -1) {
        destarr.splice(index, 1);
    }

  } else if (t == 'add') {

    // Make sure this output isn't connected already
    for (var input in matrix) {
      if (matrix.hasOwnProperty(input)) {
        for (var j=0; j<matrix[input].length; j++) {
          var outp = matrix[input][j];
          if (outp == o) {
            matrix[input].splice(j, 1);
            break;
          }
        }      
      }
    }

    // New connection!
    var destarr = matrix[i];
    var index = destarr.indexOf(o);
    if (index == -1) {
      // Not already there, so let's add it
      destarr.push(o);
    }
  }

  console.log(matrix);

}

var Input_in = function () {
  var i = get('Input');

  if (typeof i === 'object' && key in i && i.hasOwnProperty(key)) {
    var val = i[key];
    if (val in matrix && matrix.hasOwnProperty(val)) {
      var out = matrix[val];
      // Send to all connected outputs
      for (var j=0; j<out.length; j++) {
        send(out[j], i);
      }
    }
  }
}

// Copyright (c) 2014-2015 The Regents of the University of Michigan

/** RoboCafe Controller
 *
 *  @accessor RoboCafeController
 *
*/


var NUM_ROBOTS = 3;

// STATES
var STATE_IDLE = 'IDLE'; // Robot is currently just sitting there.
var STATE_SERVING = 'SERVING'; // Robot has been requested and is going servering a person.
var STATE_SPINNING = 'SPINNING';

// MAPPING OF ITEM TO ROBOT NUM
var ITEMS = {
  Twix: 0,
  SquirtGun: 1,
  BouncyBalls: 2
}

// Array of robot structs
var robots = [];




/** Define inputs and outputs. */
exports.setup = function () {
  //
  // I/O
  //
  input('UserChoice');
  input('Applause');

  output('SelectPhoneRobot');
  output('RobotStatus');



  //
  // Parameters
  //
  parameter('SpinRobotIndex', {
    type: 'number',
    value: 0
  });
  parameter('SpinRobotDuration', {
    type: 'number',
    value: 5
  });
}

exports.initialize = function () {

  // Initialize all robots
  for (var i=0; i<NUM_ROBOTS; i++) {
    var robot = {};
    robot.state = STATE_IDLE;
    robot.queue = [];
    robots[i] = robot;

    // Output initial status
    update_status(i, STATE_IDLE);
  }

  addInputHandler('UserChoice', Choice_in);
  addInputHandler('Applause', Applause_in);
}

function set_source_and_robot (phone_id, robot_index, operation) {
  var out = {
    type: operation,
    input: phone_id,
    output: robot_index
  }
  send('SelectPhoneRobot', out);
}

function update_status (robot_index, state) {
  send('RobotStatus', {
    robotid: robot_index,
    status: state
  });
}


var Choice_in = function () {
  var ws_payload = get('UserChoice');

  console.log('got choice');
  console.log(ws_payload);

  // Check if this is a normal message from a client or a status message
  if ('message' in ws_payload) {
    var msg = ws_payload.message;

    // Get the unique identifier for what sent this packet
    var phone_id = msg.phone_id;

    // Do some common operations for choice selections, cancellations,
    // and done events
    if (msg.type == 'selection' ||
        msg.type == 'cancelled' ||
        msg.type == 'finished') {

      // What item/candy the user asked for
      var selection = msg.selection;

      // Now that we know what the user wants, figure out if we can satisfy
      // the request
      if (selection in ITEMS) {
        // Get the robot struct of the one that has what we are looking for
        var rbt_idx = ITEMS[selection];
        console.log('Got robot index: '+rbt_idx + ' for ' + selection);
        var rbt = robots[rbt_idx];


        // Now do what we want if this is a new selection
        if (msg.type == 'selection') {
          // We have a robot, check its state
          if (rbt.state == STATE_IDLE) {
            // Ok great!
            // Put this one into service
            rbt.state = STATE_SERVING;
            // Keep track of which user this robot is attached to
            rbt.servicing = phone_id;
            // And send the robot to the person
            set_source_and_robot(phone_id, rbt_idx, 'add');
            // And update output status
            update_status(rbt_idx, STATE_SERVING);
          } else {
            // Robot not free, queue this request
            rbt.queue.push(phone_id);
          }


        } else if (msg.type == 'cancelled' || msg.type == 'finished') {
          // We no longer need this robot to go to this person, send it
          // back home

          // Check that the correct robot was helping this person
          if (rbt.state == STATE_SERVING && rbt.servicing == phone_id) {
            // This checks out. Stop the robot from what it was doing
            // and send it home.


            // Check if there is anyone in the queue
            if (rbt.queue.length > 0) {
              var next_phone = rbt.queue.shift();
              // Ok great!
              // Put this one into service
              rbt.state = STATE_SERVING;
              // Keep track of which user this robot is attached to
              rbt.servicing = next_phone;
              // And send the robot to the person
              set_source_and_robot(next_phone, rbt_idx, 'add');
              // And update output status
              update_status(rbt_idx, STATE_SERVING);

            } else {
              // go back home
              rbt.state = STATE_IDLE;
              rbt.servicing = null;
              set_source_and_robot(phone_id, rbt_idx, 'remove');
              // Send the robot home.
              set_source_and_robot('Home'+rbt_idx, rbt_idx, 'add');
              // // And in a couple seconds, stop telling it to go home
              // setTimeout(function () {
              //   set_source_and_robot('Home', rbt_idx, 'remove');
              // }, 3000);
              // And update output status
              update_status(rbt_idx, STATE_IDLE);

            }
          }

        }


      } else {
        console.log('Could not find a robot that matched item ' + selection);
      }

    }

  }

}

var Applause_in = function () {
  var a = get('Applause');

  if (a == 'no_applause') {
    // ignore

  } else if (a == 'some_applause') {
    // Make one robot spin

    var robot_index = parseInt(getParameter('SpinRobotIndex'));
    var robot = robots[robot_index];

    var old_servicing = robot.servicing;
    var old_state = robot.state;

    // Robot is busy with spinning!
    robot.state == STATE_SPINNING;
    update_status(robot_index, STATE_SPINNING);

    if (old_servicing != null) {
      // Stop what ever was controlling the robot before
      set_source_and_robot(old_servicing, robot_index, 'remove');
    }

    // Make it spin
    set_source_and_robot('Spin', robot_index, 'add');

    // After the spin is done, put it back
    setTimeout(function () {
      set_source_and_robot('Spin', robot_index, 'remove');
      if (old_servicing != null) {
        // Re setup what ever was controlling the robot before
        set_source_and_robot(old_servicing, robot_index, 'add');
      }

      robot.state = old_state;
      update_status(robot_index, old_state);
    }, getParameter('SpinRobotDuration') * 1000);

  }
}

// Copyright (c) 2014-2015 The Regents of the University of Michigan

/** Scarab Accessor. 
 *
 * Outputs battery charge percentage and 
 *
 *  @accessor Scarab
 */

var WebSocket = require('webSocket');

/** Set up the accessor by defining the parameters, inputs, and outputs. */
exports.setup = function() {

  input('pose');
  input('cmdvel');
  input('cancel');

  output('battery', {
    type: 'int'
  });
  output('state', {
    type: 'string'
  });
  output('location');

  parameter('server', {
    type: 'string',
    value: 'localhost'
  });
  parameter('port', {
    type: 'int',
    value: 8080
  });
  parameter('topicPrefix', {
    type: 'string',
    value: '/scarab/lucy'
  });
}

var batteryClient = null;
var stateClient = null;
var locationClient = null;
var poseClient = null;
var cmdvelClient = null;
var cancelClient = null;

var seq = 0;

/** Initializes accessor by attaching functions to inputs. */
exports.initialize = function() {

  // Retreive the current battery charge status
  batteryClient = new WebSocket.Client({
    host: getParameter('server'),
    port: getParameter('port')
  });
  batteryClient.on('open', function () {
    // Subscribe to /scarab/name/diagnostics
    batteryClient.send({
        op: "subscribe",
        topic: getParameter('topicPrefix') + '/diagnostics'
    });
  });
  batteryClient.on('message', function (msg) {
    // Quick hack to find the charge of the battery.
    // Ideally this would be done in some better way, but this is all we
    // need for now.
    s = msg.msg.status[1].message;
    charge = parseInt(s.substr(0, s.indexOf('%')));
    if (!isNaN(charge)) {
      send('battery', charge);
    }
  });
  batteryClient.on('error', function(message) {
    error(message)
  });

  // Keep track of what the robot is doing
  stateClient = new WebSocket.Client({
    host: getParameter('server'),
    port: getParameter('port')
  });
  stateClient.on('open', function () {
    // Subscribe to /scarab/name/diagnostics
    stateClient.send({
        op: "subscribe",
        topic: getParameter('topicPrefix') + '/state'
    });
  });
  stateClient.on('message', function (msg) {
    // one of: IDLE, BUSY, STUCK, FAILED
    send('state', msg.msg.state);
  });
  stateClient.on('error', function(message) {
    error(message)
  });

  // Get location updates from the robot
  locationClient = new WebSocket.Client({
    host: getParameter('server'),
    port: getParameter('port')
  });
  locationClient.on('open', function () {
    // Subscribe to /scarab/name/pose
    locationClient.send({
        op: "subscribe",
        topic: getParameter('topicPrefix') + '/pose'
    });
  });
  locationClient.on('message', function (msg) {
    send('location', msg.msg.pose);
  });
  locationClient.on('error', function(message) {
    error(message)
  });

  // Send poses to the robot
  poseClient = new WebSocket.Client({
    host: getParameter('server'),
    port: getParameter('port')
  });
  poseClient.on('open', function () {
    poseClient.send({
        op: 'advertise',
        topic: getParameter('topicPrefix') + '/goal',
        type: 'geometry_msgs/PoseStamped'
    });
  });
  poseClient.on('error', function(message) {
    error(message)
  });
  addInputHandler('pose', pose_in);

  // Send cmd_vel to the robot
  cmdvelClient = new WebSocket.Client({
    host: getParameter('server'),
    port: getParameter('port')
  });
  cmdvelClient.on('open', function () {
    cmdvelClient.send({
        op: 'advertise',
        topic: getParameter('topicPrefix') + '/cmd_vel',
        type: 'geometry_msgs/Twist'
    });
  });
  cmdvelClient.on('error', function(message) {
    error(message)
  });
  addInputHandler('cmdvel', cmdvel_in);

  // Send cancel to the robot
  cancelClient = new WebSocket.Client({
    host: getParameter('server'),
    port: getParameter('port')
  });
  cancelClient.on('open', function () {
    cancelClient.send({
        op: 'advertise',
        topic: getParameter('topicPrefix') + '/cancel',
        type: 'std_msgs/Empty'
    });
  });
  cancelClient.on('error', function(message) {
    error(message)
  });
  addInputHandler('cancel', cancel_in);
} 

var pose_in = function () {
  var v = get('pose');

  out = {
    op: 'publish',
    topic: getParameter('topicPrefix') + '/goal',
    msg: {
      'header': {
        'seq': seq++,
        'stamp': {
          'secs': 0,
          'nsecs': 0
        },
        'frame_id': 'map_hokuyo'
      },
      'pose': v
    }
  };

  poseClient.send(out);
}

var cmdvel_in = function () {
  var c = get('cmdvel');

  out = {
    op: 'publish',
    topic: getParameter('topicPrefix') + '/cmd_vel',
    msg: c
  };

  cmdvelClient.send(out);
}

var cancel_in = function () {
  var c = get('cancel');

  out = {
    op: 'publish',
    topic: getParameter('topicPrefix') + '/cancel',
    msg: {}
  };

  cancelClient.send(out);
}

exports.wrapup = function() {
  if (stateClient) {
    stateClient.removeAllListeners('open');
    stateClient.removeAllListeners('message');
    stateClient.removeAllListeners('close');
    stateClient.close();
    stateClient = null;
  }
  if (batteryClient) {
    batteryClient.removeAllListeners('open');
    batteryClient.removeAllListeners('message');
    batteryClient.removeAllListeners('close');
    batteryClient.close();
    batteryClient = null;
  }
  if (locationClient) {
    locationClient.removeAllListeners('open');
    locationClient.removeAllListeners('message');
    locationClient.removeAllListeners('close');
    locationClient.close();
    locationClient = null;
  }
  if (poseClient) {
    poseClient.send({
        op: 'unadvertise',
        topic: getParameter('topicPrefix') + '/goal'
    });
    poseClient.removeAllListeners('open');
    poseClient.removeAllListeners('message');
    poseClient.removeAllListeners('close');
    poseClient.close();
    poseClient = null;
  }
  if (cmdvelClient) {
    cmdvelClient.send({
        op: 'unadvertise',
        topic: getParameter('topicPrefix') + '/cmd_vel'
    });
    cmdvelClient.removeAllListeners('open');
    cmdvelClient.removeAllListeners('message');
    cmdvelClient.removeAllListeners('close');
    cmdvelClient.close();
    cmdvelClient = null;
  }
  if (cancelClient) {
    cancelClient.send({
        op: 'unadvertise',
        topic: getParameter('topicPrefix') + '/cancel'
    });
    cancelClient.removeAllListeners('open');
    cancelClient.removeAllListeners('message');
    cancelClient.removeAllListeners('close');
    cancelClient.close();
    cancelClient = null;
  }
}

// Copyright (c) 2014-2015 The Regents of the University of Michigan

/** Create a ROS message from an X,Y,Z coordinate.
 *
 *  @accessor Command2PoseVel
 *
*/

// Set default
var last_location = {
  position: {
    x: 0,
    y: 0,
    z: 0
  },
  orientation: {
    x: 0,
    y: 0,
    z: 0,
    w: 1
  }
};

// Variables for spin
var timer = null;
var currently_spinning;

/** Define inputs and outputs. */
exports.setup = function() {
  //
  // I/O
  //
  // Input command of what the robot should do.
  //  {
  //    command: Go|Spin
  //    X: Y: Z:   // only if Go command
  //  }
  input('Command');

  // Current location of the robot
  input('Location');

  // Direct the robot
  output('Pose');

  // Raw access
  output('CmdVel');
  output('Cancel');

  // How long to let the robot spin
  parameter('SpinDuration', {
    type: 'number'
  });
}

exports.initialize = function () {
  addInputHandler('Command', Command_in);
  addInputHandler('Location', Location_in);
}

var Command_in = function () {
  // Now parse the incoming command
  var cmd = get('Command');

  if (cmd.command == 'Go') {

    // Upon new command we need to stop any internal timers
    if (timer != null) {
      clearTimeout(timer);
    }

    // Send the robot to the given location

    var x = cmd.X || 0; 
    var y = cmd.Y || 0; 
    var z = cmd.Z || 0; 

    out = {
      'position': {
        'x': x,
        'y': y,
        'z': 0.0
      },
      'orientation': last_location.orientation
    }
    send('Pose', out);

  } else if (cmd.command == 'Spin') {
    if (!currently_spinning) {
      currently_spinning  = true;

      // Start by cancelling wherever it was going
      send('Cancel', true);

      // Wait for a quick second to make sure the cancel command has a 
      // chance to go through
      setTimeout(function () {

        // Now power the wheels to spin
        var spin_vel = {
          linear: {
            x: 0,
            y: 0,
            z: 0
          },
          angular: {
            x: 0,
            y: 0,
            z: 1.0
          }
        };

        send('CmdVel', spin_vel);
        currently_spinning  = true;

        // Now stop the spin at some point
        timer = setTimeout(function () {
          var spin_no = {
            linear: {
              x: 0,
              y: 0,
              z: 0
            },
            angular: {
              x: 0,
              y: 0,
              z: 0
            }
          };

          send('CmdVel', spin_no);
          currently_spinning = false;
          timer = null;
        }, getParameter('SpinDuration')*1000);

      }, 50);
    }

  }
}

// Save the last location
var Location_in = function () {
  last_location = get('Location');
}

// Copyright (c) 2014-2015 The Regents of the University of Michigan

/** Create a ROS message from an X,Y,Z coordinate.
 *
 *  @accessor Command2PoseVel
 *
*/

// Set default
var last_location = {
  position: {
    x: 0,
    y: 0,
    z: 0
  },
  orientation: {
    x: 0,
    y: 0,
    z: 0,
    w: 1
  }
};

// Variables for spin
var timer = null;
var currently_spinning;

/** Define inputs and outputs. */
exports.setup = function() {
  //
  // I/O
  //
  // Input command of what the robot should do.
  //  {
  //    command: Go|Spin
  //    X: Y: Z:   // only if Go command
  //  }
  input('Command');

  // Current location of the robot
  input('Location');

  // Direct the robot
  output('Pose');

  // Raw access
  output('CmdVel');
  output('Cancel');

  // How long to let the robot spin
  parameter('SpinDuration', {
    type: 'number'
  });
}

exports.initialize = function () {
  addInputHandler('Command', Command_in);
  addInputHandler('Location', Location_in);
}

var Command_in = function () {
  // Now parse the incoming command
  var cmd = get('Command');

  if (cmd.command == 'Go') {

    // Upon new command we need to stop any internal timers
    if (timer != null) {
      clearTimeout(timer);
    }

    // Send the robot to the given location

    var x = cmd.X || 0; 
    var y = cmd.Y || 0; 
    var z = cmd.Z || 0; 

    out = {
      'position': {
        'x': x,
        'y': y,
        'z': 0.0
      },
      'orientation': last_location.orientation
    }
    send('Pose', out);

  } else if (cmd.command == 'Spin') {
    if (!currently_spinning) {
      currently_spinning  = true;

      // Start by cancelling wherever it was going
      send('Cancel', true);

      // Wait for a quick second to make sure the cancel command has a 
      // chance to go through
      setTimeout(function () {

        // Now power the wheels to spin
        var spin_vel = {
          linear: {
            x: 0,
            y: 0,
            z: 0
          },
          angular: {
            x: 0,
            y: 0,
            z: 1.0
          }
        };

        send('CmdVel', spin_vel);
        currently_spinning  = true;

        // Now stop the spin at some point
        timer = setTimeout(function () {
          var spin_no = {
            linear: {
              x: 0,
              y: 0,
              z: 0
            },
            angular: {
              x: 0,
              y: 0,
              z: 0
            }
          };

          send('CmdVel', spin_no);
          currently_spinning = false;
          timer = null;
        }, getParameter('SpinDuration')*1000);

      }, 50);
    }

  }
}

// Save the last location
var Location_in = function () {
  last_location = get('Location');
}

