  //
  // I/O
  //
  output('pose');

  parameter('Period', {
    type: 'number',
    value: 1
  });
  parameter('X', {
    type: 'number',
    value: 0
  });
  parameter('Y', {
    type: 'number',
    value: 0
  });
}

exports.initialize = function () {
  pose.X = getParameter('X');
  pose.Y = getParameter('Y');

  setInterval(function () {
    send('pose', pose);
  }, getParameter('Period')*1000);
}

