// Copyright (c) 2014-2015 The Regents of the University of Michigan

/** Contain the ALPS!
 *
 *
 *  @accessor ContainerLocation
 *
*/

var boundary = [];
var boundary_single = [];


/** Define inputs and outputs. */
exports.setup = function() {
  //
  // I/O
  //
  input('LocationIn');
  output('LocationOut');

  parameter('Boundary', {
    type: 'string'
  });
}

exports.initialize = function () {
  var points = getParameter('Boundary').split(',');
  for (var i=1; i<points.length; i+=2) {
    var x = parseFloat(points[i-1]);
    var y = parseFloat(points[i]);
    boundary_single.push(x);
    boundary_single.push(y);
    boundary.push([x, y]);
  }

  addInputHandler('LocationIn', LocationIn_In);
}

function checkInside (point) {
    // ray-casting algorithm based on
    // http://www.ecse.rpi.edu/Homepages/wrf/Research/Short_Notes/pnpoly.html
    var vs = boundary;
    var x = point[0], y = point[1];

    var inside = false;
    for (var i = 0, j = vs.length - 1; i < vs.length; j = i++) {
        var xi = vs[i][0], yi = vs[i][1];
        var xj = vs[j][0], yj = vs[j][1];

        var intersect = ((yi > y) != (yj > y))
            && (x < (xj - xi) * (y - yi) / (yj - yi) + xi);
        if (intersect) inside = !inside;
    }

    return inside;
};

function offset_point (sx, sy, ex, ey) {
  var a = sx - ex;
  var b = sy - ey;
  var l = Math.sqrt((a*a)+(b*b));
  var scale = 0.3/l;

  var new_x = ex - (scale*a);
  var new_y = ey - (scale*b);

  return [new_x, new_y];
}

var LocationIn_In = function () {
  var p = get('LocationIn');
  var x = p.X;
  var y = p.Y;

  // Check if the point is inside
  if (checkInside([x, y])) {
    // Woo! Good to go, send this along
    send('LocationOut', p);
  } else {
    console.log('Point is outside ' + x + ' y: ' + y);

    // Outside, get closest point
    var closest = PolyK.ClosestEdge(boundary_single, x, y);
    var new_x = closest.point.x;
    var new_y = closest.point.y;

    // Move 30cm beyond that so we don't go right to the wall
    var shifted = offset_point(x, y, new_x, new_y);

    p.X = shifted[0];
    p.Y = shifted[1];
    send('LocationOut', p);
  }

}











