 * and to force it to be an integer. If the value argument
 * is a string, then it will be converted to a Number. */
function limit(value, low, high) {
   var parsed = parseInt(value);
   if (!parsed) {
      throw "Expected a number between " + low + " and " + high + ", but got " + value;
   }
   if (parsed < low) {
      return low;
   } else if (parsed > high) {
      return high;
   } else {
      return parsed;
   }
}

// Copyright (c) 2014-2015 The Regents of the University of Michigan

/** Outputs objects that match a key,value filter.
 *
 *  @accessor OpFilter
 *
*/

// Filter params
var key;
var value;

/** Define inputs and outputs. */
exports.setup = function() {
  //
  // I/O
  //
  input('Input', {
  });
  output('Output', {
  });

  //
  // Parameters
  //
  // key,value pair that has to be present to have a packet meet the filter
  parameter('key', {
    type: 'string'
  });
  parameter('value', {
    type: 'string'
  });
}

exports.initialize = function () {

  addInputHandler('Input', Input_in);

  key = getParameter('key');
  value = getParameter('value');
}

var Input_in = function () {
  var i = get('Input');

  if (typeof i === 'object' && key in i && i.hasOwnProperty(key) && i[key] == value) {
    send('Output', i);
  }
}

// Copyright (c) 2014-2015 The Regents of the University of Michigan

/** Strips out a certain key's value from an object as a string.
 *
 *  @accessor OpStripString
 *
*/

// Filter params
var key;

/** Define inputs and outputs. */
exports.setup = function() {
  //
  // I/O
  //
  input('Input');
  output('Output', {
    type: 'string'
  });

  //
  // Parameters
  //
  // key to extract the value from
  parameter('key', {
    type: 'string'
  });
}

exports.initialize = function () {
  addInputHandler('Input', Input_in);
  key = getParameter('key');
}

var Input_in = function () {
  var i = get('Input');

  if (typeof i === 'object' && key in i && i.hasOwnProperty(key)) {
    send('Output', ''+i[key]);
  }
}

